    #http://www.jonathanmedd.net/2014/02/testing-for-the-presence-of-a-registry-key-and-value.html
 
 } #End Function Test-RegistryValue

Function UpdateVMX{
    Param ([System.Collections.ArrayList]$VMX,
           [String] $ConfigurationItem,
           [String] $Value)

    If (($Element = (0..($VMX.Count-1)) | where {$VMX[$_] -like "*$ConfigurationItem*"}) -ne $null)
            {$VMX[$Element] = "$ConfigurationItem = `"$Value`""}
        Else{$VMX += "$ConfigurationItem = `"$Value`""}
    Return $VMX
 } #End Function VMwareUpdateVMX

Function VMWareCleanUpDisksViaGUI{
    Param ([Parameter(Mandatory=$true)][String]$VM,
           [Parameter(Mandatory=$true)][Int]$CheckForIdleInSeconds,
           [Parameter(Mandatory=$true)][Int]$MaxWaitingMinutes)

    Add-Type -AssemblyName Microsoft.VisualBasic
	Add-Type -AssemblyName System.Windows.Forms
    Add-Type @'
using System;
using System.Diagnostics;
using System.Runtime.InteropServices;

namespace PInvoke.Win32 {

    public static class UserInput {

        [DllImport("user32.dll", SetLastError=false)]
        private static extern bool GetLastInputInfo(ref LASTINPUTINFO plii);

        [StructLayout(LayoutKind.Sequential)]
        private struct LASTINPUTINFO {
            public uint cbSize;
            public int dwTime;
        }

        public static DateTime LastInput {
            get {
                DateTime bootTime = DateTime.UtcNow.AddMilliseconds(-Environment.TickCount);
                DateTime lastInput = bootTime.AddMilliseconds(LastInputTicks);
                return lastInput;
            }
        }

        public static TimeSpan IdleTime {
            get {
                return DateTime.UtcNow.Subtract(LastInput);
            }
        }

        public static int LastInputTicks {
            get {
                LASTINPUTINFO lii = new LASTINPUTINFO();
                lii.cbSize = (uint)Marshal.SizeOf(typeof(LASTINPUTINFO));
                GetLastInputInfo(ref lii);
                return lii.dwTime;
            }
        }
    }
}
'@

    Function CheckForIdleUserSession{
	    Param ([Parameter(Mandatory=$true)][Int]$IdleSeconds,
               [Int]$MaxMinutes)

	    $TimeCompare = New-TimeSpan -Seconds $IdleSeconds
        $StartTimer = [Diagnostics.Stopwatch]::StartNew()
	    $CurrentUserIsIdle = $false

        If ($MaxMinutes -eq 0) 
                {Write-Debug 'Assigning $MaxMinutes to run for 365 days.'
                 $TimeMaxMinutes = New-TimeSpan -Days 365}
            Else{Write-Debug "`$MaxMinutes: $MaxMinutes"
                 $TimeMaxMinutes = New-TimeSpan -Minutes $MaxMinutes}

	    Do {Write-Debug "Idle for $([PInvoke.Win32.UserInput]::IdleTime). Not idle for $($StartTimer.Elapsed.Seconds) seconds."
		    Start-Sleep -Seconds 5
		    $Time = [PInvoke.Win32.UserInput]::IdleTime
		    If ($StartTimer.Elapsed.TotalSeconds -gt $TimeMaxMinutes.TotalSeconds) {Return 'False'}
            If ($Time.TotalSeconds -gt $TimeCompare.Seconds) {$CurrentUserIsIdle = $true}}
	        While ($CurrentUserIsIdle -eq $false) 
	       
	        #http://stackoverflow.com/questions/15845508/get-idle-time-of-machine

	    Return 'True'

     } #End Function CheckForIdleUserSession

	Function CheckforVMFileLocks{
        Param ([Parameter(Mandatory=$true)][String]$VM)
	    
        Do {Write-Debug 'Waiting for *.lck files to be removed.'
		    Start-Sleep -Seconds 5}
		    While ((Get-ChildItem (Split-Path $VM) -Directory *.lck).Count -gt 1)
	 
	 } #End Function CheckforVMFileLocks

	$VMXFileInMemory = Get-Content $VM

    $RegEx = [RegEx]'displayName = ".*?"'
	$WorkingDisplayName = Select-String -Pattern $RegEx -InputObject $VMXFileInMemory -AllMatches | foreach {$_.matches}
	$DisplayName = $WorkingDisplayName.Value -replace 'displayName = "|"'

	Do {$ApplicationCheck = Get-Process | Where-Object {$_.MainWindowTitle -like "*$DisplayName - VMware Workstation*"} | Select-Object MainWindowTitle
	    Start-Sleep -Seconds 1}
	    While ($ApplicationCheck -eq $null)

	$VMwareWorkstation = $ApplicationCheck.MainWindowTitle

	Write-Output " Waiting for computer to be idle for $CheckForIdleInSeconds seconds before proceding."
    Write-Output " Waiting a maximum of $MaxWaitingMinutes minutes for the idle action."
    
    $IsComputerIdle = CheckForIdleUserSession -IdleSeconds $CheckForIdleInSeconds -MaxMinutes $MaxWaitingMinutes

    If ($IsComputerIdle -eq $true)
            {$PreCleanUpSize = [Math]::Round(((Get-ChildItem -path (Get-ChildItem $VM).DirectoryName -recurse | Measure-Object -property length -sum ).sum /1MB))
             
             Write-Output ' Disk cleanup has started, do not use the computer until the task is complete.'
             Write-Verbose 'Changing Window focus and sending key press for Disk Clean Up.'
             [Microsoft.VisualBasic.Interaction]::AppActivate("$VMwareWorkstation")
	         [System.Windows.Forms.SendKeys]::SendWait("%MME")
	         Write-Verbose 'Checking for file locks before proceeding.'
             CheckforVMFileLocks $VM

	         Write-Verbose "Detecting if computer is idle."
             $null = CheckForIdleUserSession -IdleSeconds $CheckForIdleInSeconds
	         Write-Verbose 'Changing Window focus and sending key press to start clean up.'
             $DetectedEAP = $ErrorActionPreference
             $ErrorActionPreference = 'SilentlyContinue'
             [Microsoft.VisualBasic.Interaction]::AppActivate('Disk Cleanup')
	         $ErrorActionPreference = $DetectedEAP
             [System.Windows.Forms.SendKeys]::SendWait("%C")
             Write-Verbose 'Checking for file locks before proceeding.'
             CheckforVMFileLocks $VM

	         Write-Verbose "Detecting if computer is idle."
             $null = CheckForIdleUserSession -IdleSeconds $CheckForIdleInSeconds
	         Write-Verbose 'Changing Window focus and sending key press to close dialogue.'
	         [Microsoft.VisualBasic.Interaction]::AppActivate('VMware Workstation')
	         [System.Windows.Forms.SendKeys]::SendWait("{ENTER}")

             $PostCleanUpSize = [Math]::Round(((Get-ChildItem -path (Get-ChildItem $VM).DirectoryName -recurse | Measure-Object -property length -sum ).sum /1MB))
             If ($PostCleanUpSize -lt $PreCleanUpSize) 
                     {Write-Verbose 'Recovered disk space successfully.'}
                 Else{Write-Warning 'Recovery of disk space was not successful, please perform this action manually.'}}
        Else{Write-Warning 'Waiting for idle computer reached the maximum waiting threshold, skipping task.'}

 } #End Function CheckforVMFileLocks
 
Function VMWarePowerControl{
    Param ([Switch] $SoftRestart,
           [Switch] $SoftStop,
           [Switch] $Start,
           [String] $Headless)

    If ($SoftRestart -eq $true)
            {Write-Output ' ⚡Restarting the virtual machine.'
             &.\vmrun -T ws reset $VM soft
             VMwareVMRunResult
             Write-Verbose 'Waiting for the virtual machine to be ready before proceeding.'
             $null = &.\vmrun -T ws -gu $GuestUserName -gp $GuestPassword listProcessesInGuest $VM
             If ($LASTEXITCODE -eq '-1')
                     {Write-Warning 'Trying to contact the virtual machine again.'
                      $null = &.\vmrun -T ws -gu $GuestUserName -gp $GuestPassword listProcessesInGuest $VM
                      VMwareVMRunResult}
             Start-Sleep -Seconds $PostBootWaitInterval}

    If ($SoftStop -eq $true)
            {Write-Output ' ⚡Stopping the virtual machine.'
             &.\vmrun -T ws stop $VM soft
             VMwareVMRunResult}

    If ($Start -eq $true)
            {Write-Output ' ⚡Starting the virtual machine.'
             Write-Debug "`$Headless: $Headless"
             If ($Headless -eq $true)
                     {&.\vmrun -T ws start $VM nogui}
                 Else{&.\vmrun -T ws start $VM gui}

             VMwareVMRunResult
             Write-Verbose 'Waiting for the virtual machine to be ready before proceeding.'
             $null = &.\vmrun -T ws -gu $GuestUserName -gp $GuestPassword listProcessesInGuest $VM
             If ($LASTEXITCODE -eq '-1')
                     {Write-Warning 'Trying to contact the virtual machine again.'
                      $null = &.\vmrun -T ws -gu $GuestUserName -gp $GuestPassword listProcessesInGuest $VM
                      VMwareVMRunResult}
             Start-Sleep -Seconds $PostBootWaitInterval}

 } #End Function VMWarePowerControl

Function VMWareSnapshotControl{
    Param ([Switch] $TakeSnapshot,
           [Switch] $RevertSnapshot,
           [Switch] $DeleteSnapshotAndChildrenAfter,
           [String] $SnapshotName)

    If ($TakeSnapshot -eq $true)
            {Write-Output " 📸Snapshot control, taking snapshot `'$SnapshotName`'."
             
             #Adding description to virtual machine annotation field, this is not the Snapshot description field.
             [System.Collections.ArrayList]$VMXFileInMemory = Get-Content "$VM"
             [System.Collections.ArrayList]$VMXFileInMemory = UpdateVMX -VMX $VMXFileInMemory -ConfigurationItem 'annotation' -Value "$SnapshotName"
             
             $WriteCheck = New-Object System.IO.FileInfo "$VM"
             Do {Try   {[IO.File]::OpenWrite($WriteCheck).Close()
                        Write-Debug 'VM file not locked.'
                        $VMXFileInMemory | Set-Content $VM -ErrorAction Stop
                        $FileNotLocked = $True}
                 Catch {$FileNotLocked = $False
                        Start-Sleep -Seconds 1
                        Write-Debug 'VM file locked.'}}
             Until ($FileNotLocked)

             &.\vmrun -T ws snapshot $VM $SnapshotName
             If ($LASTEXITCODE -eq 0)
                     {VMwareVMRunResult}
                 Else{Break}}

    IF ($DeleteSnapshotAndChildrenAfter -eq $true)
            {$DeletionSnapshotRoot = &.\vmrun -T ws -gu $GuestUserName -gp $GuestPassword listSnapshots $VM 
             $DeletionSnapshotRoot = $DeletionSnapshotRoot[$DeletionSnapshotRoot.IndexOf("$SnapshotName") + 1]
             If ($DeletionSnapshotRoot -ne $null)
                     {Write-Output ' 📸Snapshot control, deleting sub snapshot and children.'
                      &.\vmrun -T ws deleteSnapshot $VM $DeletionSnapshotRoot andDeleteChildren
                      VMwareVMRunResult}
                 Else{}
             }
    
    If ($RevertSnapshot -eq $true)
            {Write-Output " 📸Snapshot control, reverting to snapshot `'$SnapshotName`'."
             &.\vmrun -T ws revertToSnapshot $VM $SnapshotName
             VMwareVMRunResult}

 } #End Function VMWareSnapshotControl

 Function VMwareVMRunResult{
    If ($LASTEXITCODE -ne 0)
            {Write-Warning " VMRun Error: $LASTEXITCODE"}

 } #End Function VMwareVMRunResult

Function Write-Registry {
    Param ([Parameter(Mandatory=$true)][String]$RegistryKey,
           [Parameter(Mandatory=$true)][String]$RegistryValueName,
                                       [String]$RegistryValueData,
           [Parameter(Mandatory=$true)][String]$RegistryValueType,
                                       [Switch]$EnableReflectiontoWOW3264Node)
    #Need to add logic to write to WOW3264Node if in 64bit system via switch.
    
    Try   {Switch ((Get-ItemProperty -Path $RegistryKey -Name $RegistryValueName -ErrorAction SilentlyContinue).$RegistryValueName.gettype().name)
                   {'String'    {$RegistryValueTypeCheck = 'String'}
                    'Int32'     {$RegistryValueTypeCheck = 'DWord'}
                    'Int64'     {$RegistryValueTypeCheck = 'QWord'}
                    'String[]'  {$RegistryValueTypeCheck = 'MultiString'}
                    'Byte[]'    {$RegistryValueTypeCheck = 'Binary'}
                    Default     {Return 'Unable to discover registry type for overwrite check'}}}
    Catch {$RegistryValueTypeCheck = $null}

    If ($RegistryValueTypeCheck -ne $null)
            {If ($RegistryValueTypeCheck -ne $RegistryValueType)
                    {Return 'Registry type mismatch'}}
    
    #Force create the registry path.
    If (((Test-Path $RegistryKey) -replace "`n|`r") -eq 'False')
            {$null = New-Item -Path $RegistryKey -Force}

    Switch ($RegistryValueTypeCheck)
        {MultiString   {#Writing registry MultiString value.
                        $MultiStringArray = Get-ItemProperty -Path $RegistryKey | Select-Object -ExpandProperty $RegistryValueName
                        $MultiStringArray = @($MultiStringArray | where {$_ -ne $RegistryValueData})
             
                        If ($MultiStringArray -notcontains $RegistryValueData) 
                                {$MultiStringArray += $RegistryValueData}
                                 
                        #Not in use yet, needs coded into params.
                        If ($Remove -eq $true)
                                {$MultiStringArray = @($MultiStringArray | where { $_ -ne $RegistryValueData })}

                        Set-ItemProperty -Path $RegistryKey -type $RegistryValueTypeCheck -Name $RegistryValueName -Value $MultiStringArray
                                 
                        Try   {$RegistryWriteCheck = Get-ItemProperty -Path $RegistryKey | Select-Object -ExpandProperty $RegistryValueName
                               If ($RegistryWriteCheck -contains $RegistryValueData)
                                       {$RegistryWriteCheck = $null
                                        Return '0'}
                                   Else{Return 'Unexpected value on validation'}}
                        Catch {Return 'Error'}}

         Default       {#Writing registry String, Dword, Qword, value.
                        $null = New-ItemProperty -Path $RegistryKey -Name $RegistryValueName -Value $RegistryValueData -PropertyType $RegistryValueType -Force

                        Try   {$RegistryWriteCheck = (Get-ItemProperty -Path $RegistryKey | Select-Object -ExpandProperty $RegistryValueName)
                               If ($RegistryWriteCheck -eq $RegistryValueData)
                                       {$RegistryWriteCheck = $null
                                        Return '0'}
                                   Else{Return 'Unexpected value on validation'}}
                        Catch {Return 'Error'}}}

    #https://social.technet.microsoft.com/Forums/windowsserver/en-US/6ccf94ee-9a15-49fc-9abf-0050b66b0643/registry-how-to-test-the-type-of-a-value    
    #http://blogs.technet.com/b/heyscriptingguy/archive/2015/04/02/update-or-add-registry-key-value-with-powershell.aspx
    #http://stackoverflow.com/questions/27238523/editing-a-multistring-array-for-a-registry-value-in-powershell
    #http://www.jonathanmedd.net/2014/02/testing-for-the-presence-of-a-registry-key-and-value.html
    #https://msdn.microsoft.com/en-us/library/microsoft.win32.registryvaluekind(v=vs.110).aspx

 } #End Function Write-Registry

